import React, { useState } from 'react';
import { useMutation, useQueryClient} from "@tanstack/react-query";

import './Profilepage.scss'

const ProfilePage = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [address, setAddress] = useState('');
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showAccountForm, setShowAccountForm] = useState(false);
  const [showPasswordForm, setShowPasswordForm] = useState(false);


  const currentUser = JSON.parse(localStorage.getItem("currentUser"));

  const queryClient = useQueryClient();

  const handleAccountFormSubmit = (e) => {
    e.preventDefault();
    // Handle account form submission logic here
  };

  const handlePasswordFormSubmit = (e) => {
    e.preventDefault();
    // Handle password form submission logic here
  };

  return (
    <div className="profile-page">
      <div className="profile-buttons">
        <div className='image'>
        <img src={currentUser.img|| "https://www.seekpng.com/png/detail/966-9665493_my-profile-icon-blank-profile-image-circle.png"} alt="My Image" />
        </div>

        <button
        
          className="profile-button"
          onClick={() => {
            setShowAccountForm(true);
            setShowPasswordForm(false);
          }}
        >
          My Account
        </button>
        <button
          className="profile-button"
          onClick={() => {
            setShowAccountForm(false);
            setShowPasswordForm(true);
          }}
        >
          Change Password
        </button>
      </div>
      <div className="profile-card">
        {showAccountForm && (
          <div className="profile-info">
            <h2>My Account</h2>
            <form onSubmit={handleAccountFormSubmit}>
              <div className="form-group">
                <label>Name</label>
                <input
                  type="text"
                  value={name}
                  placeholder={currentUser.name}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>Phone Number</label>
                <input
                  type="tel"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>Address</label>
                <input
                  type="text"
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                />
              </div>
              <button type="submit">Save Changes</button>
            </form>
          </div>
        )}
        {showPasswordForm && (
          <div className="profile-info">
            <h2>Change Password</h2>
            <form onSubmit={handlePasswordFormSubmit}>
              <div className="form-group">
                <label>Old Password</label>
                <input
                  type="password"
                  value={oldPassword}
                  onChange={(e) => setOldPassword(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>New Password</label>
                <input
                  type="password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>Confirm New Password</label>
                <input
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
              </div>
              <button type="submit">Change Password</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
